$(function(){
	tabs.init();
});	
tabs = {
	init : function(){
		$('.tabs-1').each(function(){
			$(this).find('.tab-content-1').hide();
			$($(this).find('ul.nav .selected a').attr('href')).fadeIn(300);
			$(this).find('ul.nav a').click(function(){
				$(this).parents('.tabs-1').find('.tab-content-1').hide();
				$($(this).attr('href')).fadeIn(300);
				$(this).parent().addClass('selected').siblings().removeClass('selected');
				//Cufon.refresh();$(function(){ 
			   $('#form-2').jqTransform();			
				return false;
			});
		});
	}
}

